    // SIDEBAR TOGGLE LOGIC
function toggleSidebar() {
const sidebar = document.getElementById('sidebar');
const toggle = document.getElementById('sidebar-toggle');
sidebar.classList.toggle('active');
toggle.classList.toggle('active');
}

// CAROUSEL LOGIC
function scrollLeftID(id) {
const el = document.getElementById(id);
el.scrollBy({ left: -el.clientWidth * 0.6, behavior: 'smooth' });
}
function scrollRightID(id) {
const el = document.getElementById(id);
el.scrollBy({ left: el.clientWidth * 0.6, behavior: 'smooth' });
}

let isDragging = false, startX, scrollLeftStart, currentCarousel = null;
function dragStart(e) {
currentCarousel = e.currentTarget;
isDragging = true;
startX = e.pageX || e.touches[0].pageX;
scrollLeftStart = currentCarousel.scrollLeft;
}
function dragMove(e) {
if (!isDragging) return;
e.preventDefault();
const x = e.pageX || e.touches[0].pageX;
const walk = (startX - x);
currentCarousel.scrollLeft = scrollLeftStart + walk;
}
function dragEnd(e) {
isDragging = false;
}

// MODAL LOGIC
const modal = document.getElementById('modal');
const modalImage = document.getElementById('modal-image');
const modalTitle = document.getElementById('modal-title');
const modalDescription = document.getElementById('modal-description');
const modalTracklistContainer = document.getElementById('modal-tracklist-container');

function openModal(imageUrl, title, description, songList) {
modalImage.src = imageUrl;
modalImage.alt = title + " image";
modalTitle.textContent = title;
modalDescription.textContent = description;
modalTracklistContainer.innerHTML = '';

if (Array.isArray(songList) && songList.length > 0) {
const table = document.createElement('table');
table.classList.add('tracklist');
const thead = document.createElement('thead');
thead.innerHTML = '<tr><th>#</th><th>Title</th><th>Lyrics</th></tr>';
table.appendChild(thead);

const tbody = document.createElement('tbody');
songList.forEach((song, i) => {
const tr = document.createElement('tr');

const tdNum = document.createElement('td');
tdNum.textContent = song.trackNumber || (i+1);
tr.appendChild(tdNum);

const tdTitle = document.createElement('td');
// Logic for clickable title if lyrics exist
if (song.lyrics) {
const span = document.createElement('span');
span.textContent = song.title;
span.classList.add('clickable');
span.addEventListener('click', () => {
const nextTr = tr.nextElementSibling;
if (nextTr && nextTr.classList.contains('lyrics-row')) {
nextTr.classList.toggle('show');
}
});
tdTitle.appendChild(span);
} else {
tdTitle.textContent = song.title;
}
tr.appendChild(tdTitle);

const tdLyrics = document.createElement('td');
if (song.lyrics) {
const toggle = document.createElement('span');
toggle.textContent = "Show";
toggle.classList.add('clickable');
toggle.addEventListener('click', () => {
const nextTr = tr.nextElementSibling;
if (nextTr && nextTr.classList.contains('lyrics-row')) {
nextTr.classList.toggle('show');
}
});
tdLyrics.appendChild(toggle);
} else {
tdLyrics.textContent = "-";
}
tr.appendChild(tdLyrics);

tbody.appendChild(tr);

if (song.lyrics) {
const lyricsRow = document.createElement('tr');
lyricsRow.classList.add('lyrics-row');
const lyricsTd = document.createElement('td');
lyricsTd.colSpan = 3;
lyricsTd.textContent = song.lyrics;
lyricsRow.appendChild(lyricsTd);
tbody.appendChild(lyricsRow);
}
});

table.appendChild(tbody);
modalTracklistContainer.appendChild(table);
}

modal.classList.add('show');
}

function closeModal() {
modal.classList.remove('show');
// Clear content after animation
setTimeout(() => {
modalImage.src = '';
modalTitle.textContent = '';
modalDescription.textContent = '';
modalTracklistContainer.innerHTML = '';
}, 300);
}

function modalClick(e) {
if (e.target === modal) {
closeModal();
}
}

